/*!50003 DROP PROCEDURE IF EXISTS `record_battle_encounter` */;
DELIMITER ;;
CREATE PROCEDURE `record_battle_encounter`(
	IN bid INT,
    IN did INT,
    IN bname VARCHAR(45),
    IN stam SMALLINT)
BEGIN
	# If it fails, it's probably due to a foreign key violation (There is no record
    # of this dungeon).  Ignore the error as well as this entire operation.
	INSERT IGNORE INTO battles (id, dungeon, name, stamina, times_run) VALUES (bid, did, bname, stam, 1)
		ON DUPLICATE KEY UPDATE times_run = times_run+1;
END ;;
DELIMITER ;

/*!50003 DROP PROCEDURE IF EXISTS `record_drop_event` */;
DELIMITER ;;
CREATE PROCEDURE `record_drop_event`(
	IN battle_id INT,
    IN item_id INT,
    IN item_count INT)
BEGIN
	# If it fails, it's probably due to a foreign key violation (There is no record
    # of this battle).  Ignore the error as well as this entire operation.
	INSERT IGNORE INTO drops (battleid,itemid,count) VALUES (battle_id, item_id, item_count)
		ON DUPLICATE KEY UPDATE count=count+item_count;
END ;;
DELIMITER ;

/*!50003 DROP PROCEDURE IF EXISTS `record_dungeon_entry` */;
