CREATE PROCEDURE `record_dungeon_entry`(
	IN did INT,
    IN world_id INT,
    IN dname VARCHAR(45),
    IN dtype TINYINT,
    IN ddiff SMALLINT)
BEGIN
	# If it fails, it's because there is already an entry with this dungeon id.
